import { useRouter } from 'next/router'
import { useEffect, useState } from 'react'

const Post = () => {
  const router = useRouter()
  const { id } = router.query
  const [val, setVal] = useState("")
  const [web, setWeb] = useState("")



  function change(e) {
      setVal(e.target.value)
  }

  function send() {
      setWeb(val)
  }


// Connect to webhook upon rendering the page
  useEffect(() => {
    
    async function test() {
    let id = location.pathname.split("/")[location.pathname.split("/").length - 1];
    let ws =  new WebSocket("ws://localhost:8080/"+id);
    console.log(ws.readyState)
    ws.addEventListener("open", connected)
    ws.addEventListener("message", logMe)



    if (ws.readyState === 1) {
    ws.send(web) 
    }


    function connected (){
        console.log("connected")
    }

    function logMe(info) {
        console.log(info)
    }
     }
     test()
  }, [web])




  return <Input value={val} change={change} log={send} />
}

// Render the input and the submit button
function Input(props) {
    return (
        <div>
            <input value={props.value} onChange={props.change} />
            <button onClick={props.log}>Click me to change</button>
        </div>
    )
}

export default Post
