import styles from "../styles/homepage.module.css"
import { useState } from "react"
import { red } from "../components/Create_Join.js"

// Create the ChatRoom
function Join() {
    const [link, setLink] = useState("")
    const [test, setTest] = useState(false)

    red(link)
    /*
    function redirect() {
      if (link === "") {
        return;
      }

      if (link.includes(" ")) {
        return
      }
      location.assign(`/chat/${link}`)
    }
    */
 


    return (
      <div className={styles.join} id="#join">
        <h1>Join a chatroom</h1>
      
      <div id={styles.roomj}>
        <input value={link} onChange={(e) => setLink(e.target.value)} placeholder="Enter a chat room to join" />
        <button onClick={red}>Join</button>

      </div>
      </div>
    )
}



export default Join
