import styles from "../styles/homepage.module.css"
import { useEffect, useState } from "react"

export default function Home() {
  return  (
  <>
  <Welcome />
  <Join />
  </>
  )
}


function  Welcome() {
  return (
    <div id={styles.welcome}>
    <h1 id={styles.wc}>Welcome</h1>
    <h1 id={styles.to}>to</h1>
    <h1 id={styles.chat}>Chatify</h1>

    <div id={styles.redirect}>
    <h1>Create a chatroom</h1>
    <h1>Join a chatroom</h1>
    </div>
    
    </div>
  )
}

function Join() {
    const [link, setLink] = useState("")
    const [test, setTest] = useState(false)

    function redirect() {
      if (link === "") {
        return setTest(true)
      }

      location.assign(`/chat/${link}`)
    }
 


    return (
      <div id={styles.join}>
        <h1>Join a chatroom</h1>
      
      <div id={styles.roomj}>
        <input value={link} onChange={(e) => setLink(e.target.value)} placeholder="Enter a chat room to join" />
        <button onClick={redirect}>Join</button>
        { test && (<Empty />)}
      </div>
      </div>
    )
}

function Empty() {
  return <p>No empty input allowed</p>
}
